import React, {Component} from "react";

export default class AddAddress extends Component {

    submitAddress(event) {
        event.preventDefault();

        let address = {
            createdBy: this.refs.createdBy.value,
            email: this.refs.email.value,
            password: this.refs.password.value,

            city:this.refs.address.value,
            district:this.refs.district.value,
            province:this.refs.province.value,




        }
        fetch("http://localhost:8080/address",
            {
                method: "POST",
                headers: {
                    "content-type": "application/json",
                },
                body: JSON.stringify(address),

            })
            .then(response => response.json());
        window.location.reload();
    }

    render() {
        return (
            <div className="row">
                <form className="col s12" onSubmit={this.submitAddress.bind(this)}>
                    <div className="row">
                        <div className="input-field col s6">
                            <input placeholder="District" ref="createdBy" type="text" className="validate"/>
                            <label htmlFor="createdBy">Created By</label>
                        </div>

                        <div className="row">
                            <div className="input-field col s6">
                                <input placeholder={"Enter email address"} ref="email" type="email"
                                       className="validate"/>
                                <label htmlFor="email">Email</label>
                            </div>
                        </div>

                        <div className="input-field col s6">
                            <input placeholder="Enter password" ref="password" type="password" className="validate"/>
                            <label htmlFor="password">password</label>
                        </div>
                    </div>


                    <div className="row">
                        <button className="waves-effect waves-light btn" type="submit" name="action">Submit</button>
                    </div>


                </form>
            </div>

        )
    }
}
